package com.example.collegeapp.ui.about;

import android.os.Bundle;

import androidx.fragment.app.Fragment;
import androidx.viewpager.widget.PagerAdapter;
import androidx.viewpager.widget.ViewPager;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;

import com.bumptech.glide.Glide;
import com.example.collegeapp.R;

import java.util.ArrayList;
import java.util.List;

public class AboutFragment extends Fragment {

    private ViewPager viewPager;
    private BranchAdapter adapter;
    private List<BranchModel> list;


    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        // Inflate the layout for this fragment
        View view =  inflater.inflate(R.layout.fragment_about, container, false);

        list = new ArrayList<>();
        list.add(new BranchModel(R.drawable.programing, "Computer Engineering","A center of excellence in the domain of Computer Science & Engineering to cultivate \"digital artifacts\" for society"));
        list.add(new BranchModel(R.drawable.it, "Information Technology","A center of excellence in the domain Information Technology to cultivate \"digital artifacts\" for society"));
        list.add(new BranchModel(R.drawable.civil, "Civil Engineering","To be a centre for excellence to meet global standards satisfying dynamic demands of civil engineering industry incorporating relevant social concerns, encouraging lifelong learning,"));
        list.add(new BranchModel(R.drawable.settings, "Mechanical Engineering","To develop technicians and entrepreneurs of international standard in the area of smart renewable energy system through academic excellence, blended learning and industrial practices to sustain challenges of Industry and Society."));
        list.add(new BranchModel(R.drawable.electrical, "Electrical Engineering","To develop technicians and entrepreneurs of international standard in the area of smart renewable energy system through academic excellence, blended learning and industrial practices to sustain challenges of Industry and Society."));
        adapter = new BranchAdapter(getContext(),list);

        viewPager = view.findViewById(R.id.viewpager);
        viewPager.setAdapter(adapter);

        ImageView imageView = view.findViewById(R.id.collage_image);

        Glide.with(getContext()).load("data:image/jpeg;base64,")
                .into(imageView);
        return view;
    }
}